dict.string:
CREATE TRIGGER IF NOT EXISTS update_is_kind_of_field AFTER UPDATE OF is_song, is_audio_book, is_music_video, is_movie, is_tv_show, is_ringtone, is_podcast, is_rental ON item BEGIN
INSERT OR REPLACE INTO ext_item_view_membership(item_pid, movie_mbr, movie_rental_mbr) VALUES(new.pid, (new.is_movie AND (NOT new.is_rental) AND (NOT new.is_podcast)), (new.is_movie AND new.is_rental));
END;

CREATE TRIGGER IF NOT EXISTS update_item_sort_title AFTER UPDATE OF sort_title ON item BEGIN
        UPDATE item SET
            sort_title_section = ML3Section(sort_title)
            WHERE item.pid = NEW.pid;
END;

CREATE TRIGGER IF NOT EXISTS update_item_media_kind AFTER UPDATE OF media_kind ON item BEGIN
UPDATE item SET is_song=((new.media_kind&1)!=0), is_audio_book=((new.media_kind&8)!=0), is_music_video=((new.media_kind&32)!=0), is_movie=((new.media_kind&2)!=0), is_tv_show=((new.media_kind&64)!=0), is_ringtone=((new.media_kind&16384)!=0), is_podcast=((new.media_kind&4)!=0), is_rental=((new.media_kind&32768)!=0) WHERE pid = new.pid;
END;

CREATE TRIGGER trigger_item_collections_update_is_song AFTER UPDATE OF is_song ON item FOR EACH ROW
    BEGIN
        UPDATE album SET has_song = IFNULL((SELECT 1 FROM item WHERE is_song = 1 AND album_pid = NEW.album_pid LIMIT 1), 0) WHERE album.pid = NEW.album_pid;
        UPDATE item_artist SET has_song = IFNULL((SELECT 1 FROM item WHERE is_song = 1 AND artist_pid = NEW.artist_pid LIMIT 1), 0) WHERE item_artist.pid = NEW.artist_pid;
        UPDATE composer SET has_song = IFNULL((SELECT 1 FROM item WHERE is_song = 1 AND composer_pid = NEW.composer_pid LIMIT 1), 0) WHERE composer.pid = NEW.composer_pid;
        UPDATE genre_map SET has_song = IFNULL((SELECT 1 FROM item WHERE is_song = 1 AND genre_id = NEW.genre_id LIMIT 1), 0) WHERE genre_map.id = NEW.genre_id;
    END;

