xmlui.script:

function validateForm() {
var someVal = xmlui.getFieldValue('someVal');
var someValCorrect = false;
if (someVal == 'Secret') {
xmlui.setFieldInvalid('someVal', false);
someValCorrect = true;
} else {    
xmlui.setFieldInvalid('someVal');
someValCorrect = false;
}
var radioCorrect = false;
var radioChoice = xmlui.getFieldValue('radio');
xmlui.setFieldInvalid('or', false);
xmlui.setFieldInvalid('ap', false);
xmlui.setFieldInvalid('ba', false);
if (radioChoice == 'ba') {
radioCorrect = true;
} else {
xmlui.setFieldInvalid(radioChoice);
radioCorrect = false;
}
var dateCorrect = false;
var dateChoice = xmlui.getFieldValue('dob');
if (dateChoice - new Date('2007/01/09') == 0) {
xmlui.setFieldInvalid('dob', false);
dateCorrect = true;
} else {
xmlui.setFieldInvalid('dob');
dateCorrect = false;
}
var selectCorrect = false;
var selectChoice = xmlui.getFieldValue('selectField');
if (selectChoice == 'o') {
xmlui.setFieldInvalid('selectField', false);
selectCorrect = true;
} else {
xmlui.setFieldInvalid('selectField');
selectCorrect = false;
}
return (someValCorrect && radioCorrect && selectCorrect && dateCorrect);
}
